(function(){
	"use strict";

	angular.module("dx.webui.keyboard").factory("getKeyboardShortcutChecker", getKeyboardShortcutCheckerService);

	function getKeyboardShortcutCheckerService(
		KeyCodesEnum
	) {
		return function getKeyboardShortcutChecker(config) {
			return function keyboardShortcutChecker(event) {
				var hasModifiers = true;
				var hasKey = false;
				var i;
				for (i = config.modifiersAND.length - 1; i >= 0; i--) {
					if (!event[config.modifiersAND[i] + "Key"]) {
						hasModifiers = false;
						break;
					}
				}
				for (i = config.keysOR.length - 1; i >= 0; i--) {
					if (event.keyCode === KeyCodesEnum[config.keysOR[i]]) {
						hasKey = true;
						break;
					}
				}
				return hasModifiers && hasKey;
			};
		};
	}
	getKeyboardShortcutCheckerService.$inject = ["KeyCodesEnum"];
}());
