(function(){
	"use strict";

	angular.module("dx.webui.scroll").directive("dxScroll", scrollDirective);

	function scrollDirective() {

		var directive = {
			restrict: "A",
			controller: controller
		};

		function controller(
			$scope,
			$element
		) {
			this.$scope = $scope;
			this.$element = $element;
		}

		return directive;

	}

}());
