(function iife() {
	"use strict";

	angular.module("dx.webui.utils").factory("now", nowService);

	// TODO(kenneth): Remove this and always use performance.now, since it's supported by IE >= 10.
	function nowService(
		$window
	) {
		if ($window.performance) {
			return $window.performance.now.bind($window.performance);
		}

		return Date.now;
	}
	nowService.$inject = ["$window"];
}());
