(function iife() {
	"use strict";

	angular.module("dx.webui.answer").factory("AnswerBlock", AnswerBlockService);

	function AnswerBlockService(
			AnswerBlockTypesEnum
	) {
		var defaults = {
			blockId: 0,
			type: AnswerBlockTypesEnum.HTML,
			body: "",
			wordCount: 0,
		};

		return function AnswerBlock(data) {
			angular.extend(this, defaults, data);
		};
	}
}());
