describe("AnswerBlock", function () {
	"use strict";

	var AnswerBlock;
	var AnswerBlockTypesEnum;

	beforeEach(function() {
		module("dx.webui.answer");
		inject(function($injector) {
			AnswerBlock = $injector.get("AnswerBlock");
			AnswerBlockTypesEnum = $injector.get("AnswerBlockTypesEnum");
		});
	});

	it("has the correct default values", function() {
		var answerBlock = new AnswerBlock;
		expect(answerBlock.blockId).toEqual(0);
		expect(answerBlock.type).toEqual(AnswerBlockTypesEnum.HTML);
		expect(answerBlock.body).toEqual("");
		expect(answerBlock.wordCount).toEqual(0);
	});

	it("is extended with the data object passed to its constructor", function() {
		var data = {
			blockId: 1337,
			body: "<p>Woop woop!</p>"
		};
		var answerBlock = new AnswerBlock(data);
		expect(answerBlock.blockId).toEqual(data.blockId);
		expect(answerBlock.body).toEqual(data.body);
		// Still has the default `type`
		expect(answerBlock.type).toEqual(AnswerBlockTypesEnum.HTML);
	})
});
