describe("GradingTypeEnum", function() {
	var GradingTypeEnum;

	beforeEach(function() {
		module("dx.webui.grading");
		inject(function($injector) {
			GradingTypeEnum = $injector.get("GradingTypeEnum");
		});
	});

	it("is frozen", function() {
		expect(Object.isFrozen(GradingTypeEnum)).toBeTruthy();
	});

	it("has the correct types", function() {
		expect(GradingTypeEnum.SCORE).toEqual(0);
		expect(GradingTypeEnum.FORMATIVE).toEqual(1);
	});
});
