describe("KeyCodesEnum", function() {
	var KeyCodesEnum;

	beforeEach(function() {
		module("dx.webui.keyboard");
		inject(function($injector) {
			KeyCodesEnum = $injector.get("KeyCodesEnum");
		});
	});

	it("is frozen", function() {
		expect(Object.isFrozen(KeyCodesEnum)).toBeTruthy();
	});

	it("has the correct values", function() {
		var expectedKeyCodes = {
			a: 65,
			b: 66,
			c: 67,
			d: 68,
			e: 69,
			f: 70,
			g: 71,
			h: 72,
			i: 73,
			j: 74,
			k: 75,
			l: 76,
			m: 77,
			n: 78,
			o: 79,
			p: 80,
			q: 81,
			r: 82,
			s: 83,
			t: 84,
			u: 85,
			v: 86,
			x: 88,
			y: 89,
			z: 90,
			aring: 219,
			auml: 222,
			ouml: 186,
			0: 48,
			1: 49,
			2: 50,
			3: 51,
			4: 52,
			5: 53,
			6: 54,
			7: 55,
			8: 56,
			9: 57,
			numPad0: 96,
			numPad1: 97,
			numPad2: 98,
			numPad3: 99,
			numPad4: 100,
			numPad5: 101,
			numPad6: 102,
			numPad7: 103,
			numPad8: 104,
			numPad9: 105,
			"\\": 220, // Single backslash
			escape: 27
		};

		var actualKeys = Object.keys(KeyCodesEnum);
		var expectedKeys = Object.keys(expectedKeyCodes);

		expectedKeys.forEach(function(key) {
			if (KeyCodesEnum[key] !== expectedKeyCodes[key]) {
				throw new Error("Value of key '" + key + "' was '" + KeyCodesEnum[key] + "', it should be '" + expectedKeyCodes[key] + "'.");
			}
		});

		actualKeys.forEach(function(key) {
			if (expectedKeys.indexOf(key) === -1) {
				throw new Error("Key '" + key + "' is missing from the tests.");
			}
		});
	});
});
