(function(){
	"use strict";

	angular.module("dx.webui.sticky").directive("dxStickyGroup", stickyGroupDirective);

	function stickyGroupDirective() {

		var directive = {
			restrict: "A",
			controller: controller
		};

		function controller(
			$scope,
			$element
		) {
			this.$scope = $scope;
			this.$element = $element;
		}

		return directive;

	}

}());
