(function iife() {
	"use strict";

	// The global isNaN is not safe to use because it converts the value to a number before checking if it is NaN.
	// Number.isNaN does not have this problem, but it is not available in Internet Explorer.
	//
	// https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Number/isNaN

	angular.module("dx.webui.utils").factory("safeIsNaN", safeIsNaNService);

	function safeIsNaNService(
		$window
	) {
		function safeIsNaN(value) {
			// typeof NaN === "number"
			// So the value can't be NaN if it is not a number, and we avoid checking and converting it with isNaN.
			return typeof value === "number" && $window.isNaN(value);
		}

		return $window.Number.isNaN || safeIsNaN;
	}
}());
