/*eslint-env node*/
module.exports = function gulpTaskErrorHandler(failTaskOnError) {
	"use strict";
	return function errorHandler(error) {
		console.log("Gulp error: (" + error.plugin + ", " + error.name + ")", error.message); // eslint-disable-line no-console
		if (failTaskOnError) {
			process.exit(1);
		} else {
			// If you run `gulp watch` this will just end the task instead stopping the watch task.
			this.emit("end");
		}
	};
};
